        JRecentDisplayLabel.setFont(new java.awt.Font("Tahoma", 1, 18)); // NOI18N
        JRecentDisplayLabel.setForeground(new java.awt.Color(96, 83, 150));
        JRecentDisplayLabel.setText("Recent Runs");

        Object[][] tableData = {
                {"Empty!","Empty!","Empty!","Empty!"},
                {"Empty!","Empty!","Empty!","Empty!"}};

        if (data != null) {
            int ite = data.length-1;
            tableData = new Object[3][4];
            for (int i = 0; i < 3; i++) {
                tableData[i][0] = data[ite].getFormattedDate();
                tableData[i][1] = data[ite].getDistance();
                tableData[i][2] = data[ite].getDuration();
                tableData[i][3] = data[ite].getAltitude();
                ite--;}
        }

        String[] columnNames = {"Date", "Distance Covered (meter)", "Duration (seconds)", "Inclination (meters)"};

        recentRunsTable = new JTable(tableData, columnNames);
        recentRunsTable.setForeground(new java.awt.Color(96, 83, 150));

        jScrollPane2.setViewportView(recentRunsTable);

