    private void jImportButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jImportButtonActionPerformed
        JFileChooser chooser = new JFileChooser();
        chooser.showOpenDialog(this);
        File csvFile = chooser.getSelectedFile();
        if (csvFile == null)
            return;

        ClassData[] dataSet = CsvImporter.Import(csvFile);

        Singleton.loadedProfile.addRunDatas(dataSet);
        Singleton.dataManager.saveProfile(Singleton.loadedProfile);
        JOptionPane.showMessageDialog(null, "You data has been imported!");
        this.dispose();
        Home h = new Home();
        h.setVisible(true);
        h.pack();
        h.setLocationRelativeTo(null);
        h.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
    }//GEN-LAST:event_jImportButtonActionPerformed

